import React from 'react';
import { action } from '@storybook/addon-actions';

import Task from './Task';

//tell Storybook about the componenet we are documenting
export default {
    //the component itself
    component: Task,

    //how to refer to the component in the sidebar
    title: 'Task',

    //exports in the story file that should not be rendere as stories
    excludeStories: /.*Data$/,
};

export const taskData = {
    id: '1',
    title: 'Test Task',
    state: 'TASK_INBOX',
    updatedAt: new Date(2018, 0, 1, 9, 0),
};

//bundle all permutations of the component into a single actionsData variable
export const actionsData = {
    //action() allows creating a callback that appears in the actions panel of the storybook UI when clicked
    onPinTask: action('onPinTask'),
    onArchiveTask: action('onArchiveTask'),
};

export const Default = () => <Task task={{ ...taskData}} {...actionsData} />;

export const Pinned = () => <Task task ={{ ...taskData, state: 'TASK_PINNED' }} {...actionsData} />

export const Archived = () => (
    <Task task ={{ ...taskData, state: 'TASK_ARCHIVED'}} {...actionsData} />
);
